CREATE VIEW `school_trs_view` AS select `schools`.`id` AS `id`,`schools`.`name` AS `name`,`schools`.`account_type` AS `account_type`,`schools`.`from_date` AS `from_date`,`schools`.`to_date` AS `to_date`,`users`.`teacher id` AS `teacher id`,`users`.`username` AS `username`,`users`.`password` AS `password`,`users`.`fullname` AS `fullname`,`users`.`school` AS `school` from (`schools` join `users`) where (`schools`.`id` = `users`.`school`);
